    setDays([...days, newDay])
    console.log(days)

  }



  const deleteTask = (id) => {
    console.log("delete, id");
  };
  return (
    <div className="container">
      <Header title="Daily Planner" />
      {currDate}
      <AddTask onAdd={addTask}/>
      <AddEntry onAddEntry={addEntry} />
      <Day tasks={today[0]} entries={today[1]} onDelete={deleteTask}/>
    </div>
  );
}
