  };
  return (
    <div className="container">
      <Header title="Daily Planner" />
      {currDate}
      <AddTask onAdd={addTask}/>
      <AddEntry onAddEntry={addEntry} />
      <Day tasks={tasks} entries={entries} onDelete={deleteTask}/>
    </div>
  );
}
